'use strict';
const Core = require('./Core');
/**
 * Log class for request to the customer services with base path /logs
 * @extends Core
 */
class Log extends Core {
    /**
     * @memberof Core
     * @constructor
     */
    constructor(prop) {
        super(prop);
        this._path = '/logs';
        this._token = {};
    }
    _save() {
        let method = 'post',
            path = this._id ? this._path + '/' + this._id : this._path;
        return new Promise((resolve, reject) => {
            this.api(path, method, this._properties).then((res) => {
                if (res && typeof res === 'object') {
                    this.set(res);
                    delete this._id;
                }
                return resolve(res);
            }).catch(function (err) {
                return reject(err);
            });
        });
    }
}
module.exports = Log;